            console.log(responsejson);





              dispatch(
                cartSliceActions.add({
                  title: title,
                  price: price,
                  total: 20,
                  quantity: 10,
                })
              );
            }}
          >
            Add to Cart
          </button>
        </div>
      </Card>
    </li>
  );
}

export default ProductItem;
