import { createSlice } from "@reduxjs/toolkit";

const initialState = {
  items: [
    { id: 1, title: "Test Item", quantity: 3, total: 18, price: 6 },
    { id: 2, title: "Lifebuoy", quantity: 10, total: 150, price: 50 },
    { id: 2, title: "Lifebuoy 1", quantity: 10, total: 500, price: 50 },
    { id: 2, title: "Lifebuoy 2", quantity: 3, total: 150, price: 50 },
    { id: 2, title: "Lifebuoy 3", quantity: 10, total: 500, price: 50 },
  ],
  totalCost: 0,
};

const calculateTotalCost = () => {
  let temp = 0;
  for (let i = 0; i < state.items.length; i++) {
    temp = state.items[i].total + temp;
  }
  return temp;
};

const cartSlice = createSlice({
  name: "cart",
  initialState,
  reducers: {
    add(state, action) {
      const itemCount = state.items.length + 1;
      state.items = [...state.items, { ...action.payload, id: itemCount }];
    },
    decreaseItem(state, action) {
      console.log("decreaseItem id =>", action.payload);
      for (let i = 0; i < state.items.length; i++) {
        if (state.items[i].id === action.payload) {
          state.items[i].quantity--;
          state.items[i].total = state.items[i].quantity * state.items[i].price;
          if (state.items[i].quantity == 0) {
            const index = i;
            if (index > -1) {
              state.items.splice(index, 1);
            }
            console.log(state.items);
            state.items = state.items.splice(i, i + 1);
          }
        }
      }
      state.totalCost = calculateTotalCost(state)
    },
    increaseItem(state, action) {
      console.log("increaseItem id =>", action.payload);

      for (let i = 0; i < state.items.length; i++) {
        if (state.items[i].id === action.payload) {
          state.items[i].quantity++;
          state.items[i].total = state.items[i].quantity * state.items[i].price;
          break;
        }
      }
    },
  },
});

export const cartSliceActions = cartSlice.actions;

export default cartSlice.reducer;
